(15, 'İş rejimi və əlaqə', 'İş rejimi və əlaqə', '', '<p style=\"font-family: Calibri; font-size: 18px; font-weight: normal; color: #ff4200;\">İş rejimi</p>\r\n<br />\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\"><span style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ff4200;\">Həftənin b&uuml;t&uuml;n g&uuml;nləri 10:00 - dan 18:00 - dək, ekspozisiya 10:00-dan 17:00-dək</span></p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">* 1 yanvar istisna olmaqla</p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">&Uuml;nvan:</p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">Bakı şəhəri, Qaradağ rayonu, Qobustan qəsəbəsi, AZ1080</p>\r\n<br />\r\n<p style=\"font-family: Calibri; font-size: 18px; font-weight: normal; color: #ff4200;\">Sosial media</p>\r\n<br />\r\n<div class=\"link_stil\"><a href=\"https://www.facebook.com/gobustan.rockart/\" target=\"_blank\">Facebook</a></div>\r\n<div class=\"link_stil\"><a href=\"https://www.youtube.com/channel/UCBSRRMXK5QERtexj_JA5Cjw/\" target=\"_blank\">Youtube</a></div>\r\n<div class=\"link_stil\"><a href=\"https://www.instagram.com/gobustan.preserve/\" target=\"_blank\">Instagram</a></div>\r\n<br />\r\n<p style=\"font-family: Calibri; font-size: 18px; font-weight: normal; color: #ff4200;\">Əlaqə</p>\r\n<br />\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">Tel: (+99412) 544 66 22/27</p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">Sayt: www.gobustan-rockart.az</p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">e-mail: office@gobustan-rockart.az</p>', '/az/contact/15', '', 'pages-thumb-15.png', 1, 15, 325, 613, 2, '2019-10-10 09:42:56', 'az', 2),
(15, 'Contacts', 'Contacts', '', '<p style=\"font-family: Calibri; font-size: 18px; font-weight: normal; color: #ff4200;\">Working regime</p>\r\n<br />\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\"><span style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ff4200;\"><span>All days of the week, from 10:00 to 18:00, exposition from 10:00 to 17:00</span></span></p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">* expect 1st January</p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">Address:</p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">Baku city, Garadagh district, Gobustan settlement, AZ1080</p>\r\n<br />\r\n<p style=\"font-family: Calibri; font-size: 18px; font-weight: normal; color: #ff4200;\">Social media</p>\r\n<br />\r\n<div class=\"link_stil\"><a href=\"https://www.facebook.com/gobustan.rockart/\" target=\"_blank\">Facebook</a></div>\r\n<div class=\"link_stil\"><a href=\"https://www.youtube.com/channel/UCBSRRMXK5QERtexj_JA5Cjw/\" target=\"_blank\">Youtube</a></div>\r\n<div class=\"link_stil\"><a href=\"https://www.instagram.com/gobustan.preserve/\" target=\"_blank\">Instagram</a></div>\r\n<br />\r\n<p style=\"font-family: Calibri; font-size: 18px; font-weight: normal; color: #ff4200;\">Contact</p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">Tel: (+99412) 544 66 27/22</p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">Web: www.gobustan-rockart.az</p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">e-mail: office@gobustan-rockart.az</p>', '/en/contact/15', '', 'pages-thumb-15.png', 1, 15, 325, 613, 2, '2019-10-10 09:42:56', 'en', 2),
(15, 'Контакты', 'Контакты', '', '<p style=\"font-family: Calibri; font-size: 18px; font-weight: normal; color: #ff4200;\">Режим работы</p>\r\n<br />\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\"><span style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ff4200;\"><span>Все дни недели, с 10:00 до 18:00, экспозиция с 10:00 до 17:00</span></span></p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">* за исключением 1-го января</p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">Адрес:</p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">Баку, Карадагский район, посёлок Гобустан, AZ1080</p>\r\n<br />\r\n<p style=\"font-family: Calibri; font-size: 18px; font-weight: normal; color: #ff4200;\">Социальные сети</p>\r\n<br />\r\n<div class=\"link_stil\"><a href=\"https://www.facebook.com/gobustan.rockart/\" target=\"_blank\">Facebook</a></div>\r\n<div class=\"link_stil\"><a href=\"https://www.youtube.com/channel/UCBSRRMXK5QERtexj_JA5Cjw/\" target=\"_blank\">Youtube</a></div>\r\n<div class=\"link_stil\"><a href=\"https://www.instagram.com/gobustan.preserve/\" target=\"_blank\">Instagram</a></div>\r\n<br />\r\n<p style=\"font-family: Calibri; font-size: 18px; font-weight: normal; color: #ff4200;\">Контакт</p>\r\n<br />\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">Tel: (+99412) 544 66 27/22</p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">Сайт: www.gobustan-rockart.az</p>\r\n<p style=\"font-family: Calibri; font-size: 14px; font-weight: normal; color: #ffffff;\">эл. почта: office@gobustan-rockart.az</p>', '/ru/contact/15', '', 'pages-thumb-15.png', 1, 15, 325, 613, 2, '2019-10-10 09:42:56', 'ru', 2),
